import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:google_fonts/google_fonts.dart';
import 'package:intl/intl.dart';
import '../providers/leave_provider.dart';

class LeaveApplicationScreen extends ConsumerStatefulWidget {
  const LeaveApplicationScreen({super.key});

  @override
  _LeaveApplicationScreenState createState() => _LeaveApplicationScreenState();
}

class _LeaveApplicationScreenState extends ConsumerState<LeaveApplicationScreen> {
  final _reasonController = TextEditingController();
  String _selectedLeaveType = "Sick Leave";
  DateTime? _startDate;
  DateTime? _endDate;

  Future<void> _selectDate(BuildContext context, bool isStart) async {
    final DateTime? picked = await showDatePicker(
      context: context,
      initialDate: DateTime.now(),
      firstDate: DateTime(2022),
      lastDate: DateTime(2030),
    );
    if (picked != null) {
      setState(() {
        if (isStart) {
          _startDate = picked;
        } else {
          _endDate = picked;
        }
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    final isLoading = ref.watch(leaveRequestProvider);

    return Scaffold(
      appBar: AppBar(
        title: Text(
          "Apply for Leave",
          style: GoogleFonts.poppins(fontSize: 20, fontWeight: FontWeight.bold),
        ),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text("Select Leave Type:", style: GoogleFonts.poppins(fontSize: 16)),
            DropdownButton<String>(
              value: _selectedLeaveType,
              onChanged: (String? newValue) {
                setState(() {
                  _selectedLeaveType = newValue!;
                });
              },
              items: ["Sick Leave", "Casual Leave", "Annual Leave", "Emergency Leave"]
                  .map((String leave) => DropdownMenuItem(
                        value: leave,
                        child: Text(leave),
                      ))
                  .toList(),
            ),
            const SizedBox(height: 10),
            Text("Select Leave Dates:", style: GoogleFonts.poppins(fontSize: 16)),
            Row(
              children: [
                Expanded(
                  child: TextButton(
                    onPressed: () => _selectDate(context, true),
                    child: Text(_startDate == null
                        ? "Select Start Date"
                        : DateFormat('yyyy-MM-dd').format(_startDate!)),
                  ),
                ),
                Expanded(
                  child: TextButton(
                    onPressed: () => _selectDate(context, false),
                    child: Text(_endDate == null
                        ? "Select End Date"
                        : DateFormat('yyyy-MM-dd').format(_endDate!)),
                  ),
                ),
              ],
            ),
            const SizedBox(height: 10),
            Text("Reason:", style: GoogleFonts.poppins(fontSize: 16)),
            TextField(
              controller: _reasonController,
              maxLines: 3,
              decoration: const InputDecoration(border: OutlineInputBorder()),
            ),
            const SizedBox(height: 20),
            isLoading
                ? const Center(child: CircularProgressIndicator())
                : ElevatedButton(
                    onPressed: () {
                      if (_startDate != null && _endDate != null && _reasonController.text.isNotEmpty) {
                        ref.read(leaveRequestProvider.notifier).applyLeave(
                              leaveType: _selectedLeaveType,
                              reason: _reasonController.text,
                              startDate: DateFormat('yyyy-MM-dd').format(_startDate!),
                              endDate: DateFormat('yyyy-MM-dd').format(_endDate!),
                            );
                        _reasonController.clear();
                      }
                    },
                    child: Text("Submit Leave Request", style: GoogleFonts.poppins(fontSize: 16)),
                  ),
          ],
        ),
      ),
    );
  }
}
